formulas.expression:
IF(
  ISBLANK(TRIM(IF({!Billing_Add_Same_as_Shipping}, {!Full_Name_Ship}, {!Full_Name_Bill}))),
  "",
  IF(
    /* space count = 0 â†’ only one word */
    LEN(TRIM(IF({!Billing_Add_Same_as_Shipping}, {!Full_Name_Ship}, {!Full_Name_Bill}))) 
      - LEN(SUBSTITUTE(TRIM(IF({!Billing_Add_Same_as_Shipping}, {!Full_Name_Ship}, {!Full_Name_Bill})), " ", "")) = 0,
    /* return the single word */
    TRIM(IF({!Billing_Add_Same_as_Shipping}, {!Full_Name_Ship}, {!Full_Name_Bill})),
    /* else return only the 2nd word */
    MID(
      TRIM(IF({!Billing_Add_Same_as_Shipping}, {!Full_Name_Ship}, {!Full_Name_Bill})) & " ",
      FIND(" ", TRIM(IF({!Billing_Add_Same_as_Shipping}, {!Full_Name_Ship}, {!Full_Name_Bill})) & " ") + 1,
      FIND(
        " ",
        TRIM(IF({!Billing_Add_Same_as_Shipping}, {!Full_Name_Ship}, {!Full_Name_Bill})) & " ",
        FIND(" ", TRIM(IF({!Billing_Add_Same_as_Shipping}, {!Full_Name_Ship}, {!Full_Name_Bill})) & " ") + 1
      )
      - FIND(" ", TRIM(IF({!Billing_Add_Same_as_Shipping}, {!Full_Name_Ship}, {!Full_Name_Bill})) & " ") - 1
    )
  )
)
